## Oregon Trail Game with controller
## 
## https://arcade.makecode.com/S71753-71277-78947-73104 
TRAVEL_MILES = 0
TRAVEL_DAYS = 0
TOTAL_FOOD = 500
MAX_DAYS = 300
def show_status(show):

    global TRAVEL_DAYS
    global TRAVEL_MILES
    global TOTAL_FOOD

    msg = "Day "+ TRAVEL_DAYS + " Miles: " + TRAVEL_MILES + " Food left: "+ TOTAL_FOOD
    print(msg)
    if show == True:
        game.show_long_text(msg, DialogLayout.CENTER)


def check_game():
    global TRAVEL_DAYS
    global TOTAL_FOOD
    global TRAVEL_MILES
    global MAX_DAYS 

    show_status(False)
    game.set_game_over_effect(True, effects.confetti)
   
    if TOTAL_FOOD <= 0:
        game.splash("No more FOOD!! You lost!!")
        game.game_over(False)
    if TRAVEL_MILES >= 50:
        game.game_over(True)
        game.splash("You Won!!")
    if TRAVEL_DAYS >= 300:
        game.splash("You lost!!")
        game.game_over(False)


def add_days(day):
    global TRAVEL_DAYS
    TRAVEL_DAYS = TRAVEL_DAYS + day

def eat_food(days):
    global TOTAL_FOOD
    TOTAL_FOOD = TOTAL_FOOD - (days * 10)

def rest_day():
    days = game.ask_for_number("How many rest days do you want?")
    eat_food(days)
    add_days(days)
    message = days + "days Resting"
    print("Rest Day :" + message )

    check_game()

def add_miles():
    global TRAVEL_MILES
    today_miles = randint(10, 20)
    TRAVEL_MILES = TRAVEL_MILES + today_miles

def travel_day():
    eat_food(1)
    add_days(1)
    add_miles()
    check_game()

